var exports={};

var target='dev';
var argv = process.argv;
var kvs = {};
for(var key in argv)
{
    if(key > 1)
    {
        var kv = argv[key].split("=");
        kvs[kv[0]] = kv[1];
    }
}
if(kvs.target)
{
    target = kvs.target;
}


if(target=='run'){
    exports.dbName='printerTest';
    exports.mongodbUrl='192.168.0.20';
    exports.mongodbPort=27017;
}else{
    exports.dbName='printerTest';
    exports.mongodbUrl='192.168.178.1';
    exports.mongodbPort=27017;

}




module.exports = exports;




