var exports={};

var target='dev';
var argv = process.argv;
var kvs = {};
for(var key in argv)
{
    if(key > 1)
    {
        var kv = argv[key].split("=");
        kvs[kv[0]] = kv[1];
    }
}
if(kvs.target)
{
    target = kvs.target;
}
var HOST = '101.200.232.194';
var PORT = 16777;

if(target=='run'){
    exports.dbName='printerTest';
    exports.mongodbUrl='192.168.0.20';
    exports.mongodbPort=27017;
    HOST = '192.168.0.19';
    PORT = 16777;

}else if(target=='dev'){
    exports.dbName='printerTest';
    exports.mongodbUrl='127.0.0.1';
    exports.mongodbPort=27017;
    HOST = '127.0.0.1';
    PORT = 16777;
}else if(target=='test'){
    exports.dbName='printerTest';
    exports.mongodbUrl='218.30.107.20';
    exports.mongodbPort=27017;
    HOST = '192.168.0.19';
    PORT = 16777;
}
exports.HOST = HOST;
exports.PORT = PORT;

var node_plat_https={
    'devOpt': {
        hostname: '127.0.0.1',
        port: 9088,
        path: '/mcp-filter/main/interface.htm',
        method: 'POST'
    },
    'runOpt': {
        hostname: '192.168.0.19',
        port: 9088,
        path: '/mcp-filter/main/interface.htm',
        method: 'POST'
    }
};


if(target == 'dev')
{
    exports.node_plat_opt = node_plat_https.devOpt;
}
else if(target == 'run')
{
    exports.node_plat_opt = node_plat_https.runOpt;
}


module.exports = exports;




