var mongoDBUtil = require("./MongoDBUtil.js");

var Docs = function(){};

Docs.prototype.findAll = function(collectionName,cb) {
	mongoDBUtil.db.collection(collectionName, function(err,collection){
		if(err){
			cb(err);
		}else{
			collection.find({}).toArray(function(err,docs){
				cb(err,docs);
			});
		}
	});
};

module.exports = Docs;